import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'OpenERX';
  public todos: any[] = [];

  /**
   *
   */
  constructor() {

    this.todos.push(1);
    this.todos.push(2);
    this.todos.push(3);
    this.todos.push(4);
    this.todos.push(5);
    this.todos.push(6);

  }
}
